/// <reference types="@wdio/sync/webdriverio-core" />
/// <reference types="@wdio/sync" />
export default class PerformanceTotalService {
    browser: WebdriverIO.BrowserObject;
    _serviceOptions: {
        disableAppendToExistingFile: boolean;
        performanceResultsFileName: string;
        dropResultsFromFailedTest: boolean;
    };
    /**
     * `serviceOptions` contains all options specific to the service
     * e.g. if defined as follows:
     *
     * ```
     * services: [['custom', { foo: 'bar' }]]
     * ```
     *
     * the `serviceOptions` parameter will be: `{ foo: 'bar' }`
     */
    constructor(serviceOptions: {
        disableAppendToExistingFile: boolean;
        performanceResultsFileName: string;
        dropResultsFromFailedTest: boolean;
    }, capabilities: any, config: any, browser: WebdriverIO.BrowserObject);
    before(config: any, capabilities: any): void;
    beforeTest(test: any, context: any): Promise<void>;
    afterTest(test: any, context: any, { error, result, duration, passed, retries }: any): void;
    after(exitCode: any, config: any, capabilities: any): Promise<void>;
}
